const titoli = ['SPORT', 'ATTUALITÃ€', 'NEWS'];
const testi = [
    'Tutto lo Sport: news, video e risultati su calcio di Serie A, Calciomercato, Champions League, Europa League, MotoGP, Basket, F1',
    'Tutte le ultime notizie di oggi. Approfondimenti, editoriali, foto e video da Il Sole 24 ORE.',
    'Copertura giornalistica completa e aggiornata ottenuta combinando fonti di notizie in tutto il mondo attraverso Google News.'
];

const images = [
    'data:image/jpeg;base64,',
    'data:image/jpeg;base64,',
    'data:image/jpeg;base64,'
];

let currentIndex = 0;


const titololiList = document.querySelector('.titolo');
const testiList = document.querySelector('.testo');
const boxImg = document.querySelector('.box-img');

const button = document.querySelector('.next');


titololiList.innerHTML = titoli[currentIndex];
testiList.innerHTML = testi[currentIndex];
boxImg.src = images[currentIndex];

button.addEventListener('click', function() {
    currentIndex++;
    if (currentIndex > titoli.length - 1) {
        currentIndex = 0;
    }
    if (currentIndex > testi.length - 1) {
        currentIndex = 0;
    }
    if (currentIndex > images.length - 1) {
        currentIndex = 0;
    }
    titololiList = titoli[currentIndex];
    testiList = testi[currentIndex];
    boxImg = images[currentIndex];
});